import Chess from 'react-chess'
import myRandomMoves from './RandomMoves'

const Board = () => {

    const [position, handlePosition] = useState(['R@h1']);

    const makeMove = () => {

        handlePosition(['R@h8']);


    }

    const makeMoveBack = () => {

        handlePosition(['R@h1']);

    }

    const makeRandomMove = () => {







        handlePosition(myRandomMoves());


    }

    return (

        <div >
            <button onClick={() => makeMove()} >
                Move
            </button>

            <button onClick={() => makeMoveBack()} >
                Back
            </button>
            <button onClick={() => makeRandomMove()} >
                Random
            </button>

            <Chess
                pieces={position}

                allowMoves={false} />
        </div>
    )


}

export default Board;
