import React, { useState } from 'react';
import Chess from 'react-chess';

const BishopDarkSquares = () => {

    //black squares

    //short diagonals
    const mov1BB = ['a1'];
    const mov2BB = ['a3', 'b2', 'c1'];
    const mov3BB = ['a5', 'b4', 'c3', 'd2', 'e1'];
    const mov4BB = ['a7', 'b6', 'c5', 'd4', 'e3', 'f2', 'g1'];
    const mov5BB = ['b8', 'c7', 'd6', 'e5', 'f4', 'g3', 'Bh2'];
    const mov6BB = ['d8', 'e7', 'f6', 'g5', 'h4'];
    const mov7BB = ['f8', 'g7', 'h6'];
    const mov8BB = ['h8'];

    //long diagonals 
    const mov1GDBB = ['g1', 'h2'];
    const mov2GDBB = ['e1', 'f2', 'g3', 'h4'];
    const mov3GDBB = ['c1', 'd2', 'e3', 'f4', 'g5', 'h6'];
    const mov4GDBB = ['a1', 'b2', 'c3', 'd4', 'e5', 'f6', 'g7', 'h8'];
    const mov5GDBB = ['a3', 'b4', 'c5', 'd6', 'e7', 'f8'];
    const mov6GDBB = ['a5', 'b6', 'c7', 'd8'];
    const mov7GDBB = ['a7', 'b8'];

    const allMoves = [mov1BB, mov2BB, mov3BB, mov4BB, mov5BB, mov6BB, mov7BB, mov8BB];
    const allMoves2 = [mov1GDBB, mov2GDBB, mov3GDBB, mov4GDBB, mov5GDBB, mov6GDBB, mov7GDBB];

    const randomMoveBishopDarkSquares = (array) => {

        for (let index = 0; index < array.length; index++) {

            const cond = diagonal.some((val) => array[index].indexOf(val) !== -1);

            if (cond === true) {
                const d = array[index];
                const n = Math.floor(Math.random() * d.length);
                setDiagonal([d[n]]);
            }
        }
    }

    const makeBishopDarkSquaresMoveDarkSquares = () => {

        if (Number(Math.random()) > 0.49) {
            randomMoveBishopDarkSquares(allMoves2);
        }

        else {
            randomMoveBishopDarkSquares(allMoves);
        }

        handlePosition([piece + diagonal]);
        console.log(position, diagonal);
    }
}


const BishopLightSquares = () => {
    const [diagonal, setDiagonal] = useState(['e5']);

    //black squares

    //short diagonals
    const mov1BB = ['a8'];
    const mov2BB = ['a6', 'b7', 'c8'];
    const mov3BB = ['a4', 'b5', 'c6', 'd7', 'e8'];
    const mov4BB = ['a2', 'b3', 'c4', 'd4', 'e6', 'f7', 'g8'];
    const mov5BB = ['b1', 'c2', 'd3', 'e4', 'f5', 'g6', 'h7'];
    const mov6BB = ['d1', 'e2', 'f3', 'g4', 'h5'];
    const mov7BB = ['f1', 'g2', 'h3'];
    const mov8BB = ['h1'];

    //long diagonals 
    const mov1GDBB = ['g8', 'h7'];
    const mov2GDBB = ['e8', 'f7', 'g6', 'h5'];
    const mov3GDBB = ['c8', 'd7', 'e6', 'f5', 'g4', 'h3'];
    const mov4GDBB = ['a8', 'b7', 'c6', 'd5', 'e4', 'f3', 'g2', 'h1'];
    const mov5GDBB = ['a6', 'b5', 'c4', 'd3', 'e2', 'f1'];
    const mov6GDBB = ['a4', 'b3', 'c2', 'd1'];
    const mov7GDBB = ['a2', 'b1'];

    const allMoves = [mov1BB, mov2BB, mov3BB, mov4BB, mov5BB, mov6BB, mov7BB, mov8BB];
    const allMoves2 = [mov1GDBB, mov2GDBB, mov3GDBB, mov4GDBB, mov5GDBB, mov6GDBB, mov7GDBB];

    const randomMoveBishopLightSquares = (array) => {

        for (let index = 0; index < array.length; index++) {

            const cond = diagonal.some((val) => array[index].indexOf(val) !== -1);

            if (cond === true) {
                const d = array[index];
                const n = Math.floor(Math.random() * d.length);
                setDiagonal([d[n]]);
            }
        }
    }

    const makeBishopLightSquaresMoveLightSquares = () => {

        if (Number(Math.random()) > 0.49) {
            randomMoveBishopLightSquares(allMoves2);
        }

        else {
            randomMoveBishopLightSquares(allMoves);
        }

        handlePosition([piece + diagonal]);
        console.log(position, diagonal);
    }

    makeBishopLightSquaresMoveLightSquares()

}




const Queen = () => {
    const letters = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
    const numbers = ['1', '2', '3', '4', '5', '6', '7', '8'];
    const [letter, setLetter] = useState(1);
    const [number, setNumber] = useState(1);
    const [piece] = useState(['Q@']);
    const [position, handlePosition] = useState(['Q@a1']);







    const randomNumber = (array) => { return Math.floor(Math.random() * array.length); }







    const makeQueenMove = () => {



        if (Number(Math.random()) > 0.49) {



            if (Number(Math.random()) > 0.49) {
                setNumber(randomNumber(letters));

            }
            else {
                setLetter(randomNumber(numbers));
            }

        } else {

            BishopDarkSquares();
        }

        handlePosition([piece + letters[letter] + numbers[number]]);

    }


    return (
        <div >
            <button onClick={(e) => makeQueenMove(e.target.value)} > Move </button>
            <hr />
            <Chess pieces={position} allowMoves={false} />

        </div>
    )
}


















export default Queen;
