const myRandomPositions = () => {

    function getRandomInt(max) {
        return Math.floor(Math.random() * Math.floor(max));
    }

    const chessPieces = ['K', 'k', 'R', 'Q', 'B', 'N', 'r', 'q', 'b', 'n', 'R', 'B', 'N', 'r', 'b', 'n', 'P', 'P', 'P', 'P', 'P', 'P', 'P', 'P', 'p', 'p', 'p', 'p', 'p', 'p', 'p', 'p'];
    const letters = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
    const numbers = ['1', '2', '3', '4', '5', '6', '7', '8'];

    //Set Board and Coordenates
    const randomNumbers = () => getRandomInt(numbers.length);
    const randomLetters = () => getRandomInt(letters.length);
    const setBoard = [];
    const randomCoordenates = (k) => {
        const s = [];
        for (let [i, k] = 0; i < 40; i++) {
            s.push(letters[randomLetters()] + numbers[randomNumbers()])

        }
        const coo = [...new Set(s), k];

        for (let i = 0; i < chessPieces.length; i++) {
            setBoard.push(chessPieces[i] + '@' + coo[i])

        }

        // const forbittenSquares = ['P@a1', 'P@b1', 'P@c1', 'P@d1', 'P@e1', 'P@f1', 'P@g1', 'P@h1', 'P@a8', 'P@b8', 'P@c8', 'P@d8', 'P@e8', 'P@f8', 'P@g8', 'P@h8', 'P@a7', 'P@b7', 'P@c7', 'P@d7', 'P@e7', 'P@f7', 'P@g7', 'P@h7', 'p@a1', 'p@b1', 'p@c1', 'p@d1', 'p@e1', 'p@f1', 'p@g1', 'p@h1', 'p@a8', 'p@b8', 'p@c8', 'p@d8', 'p@e8', 'p@f8', 'p@g8', 'p@h8', 'p@a2', 'p@b2', 'p@c2', 'p@d2', 'p@e2', 'p@f2', 'p@g2', 'p@h2'];
        // const filtered = [];


        return setBoard;
    }


    return randomCoordenates();
}

export default myRandomPositions;
