import React, { useState } from 'react';
import Chess from 'react-chess';

const Bishop = () => {
    const letters = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
    const [letter, setLetter] = useState(['a']);
    const [number, setNumber] = useState(Number(1));
    const [piece] = useState(['B@']);
    const [index, setIndex] = useState(Number(1));
    const [position, handlePosition] = useState(['B@a1']);

    const makeBishopMove = () => {

        const randomMove = () => {
            if (Number(Math.random()) > 0.49) {
                setNumber(Number(number) + 1);
                setIndex(index + 1);
            }
            else {
                setIndex(index - 1)
                setIndex(index - 1)
            }


            setLetter(letters[Number(index)])
            handlePosition([piece + letter + number]);
            console.log(position);

        }
        randomMove();

    }

    return (
        <div >
            <button onClick={(e) => makeBishopMove(e.target.value)} > Move </button>
            <hr />
            <Chess pieces={position} allowMoves={false} />

        </div>
    )
}

export default Bishop;
