                    if (Number(Math.random()) > 0.49) {
                        setNumber(Number(number) + 2);
                        setIndex(index + 1);

                    }
                    else {
                        setNumber(Number(number) + 2);
                        setIndex(index - 1);


                    }
                }
                else {
                    if (Number(Math.random()) > 0.49) {
                        setNumber(Number(number) + 1);
                        setIndex(index + 2);

                    }
