import React from 'react';
import Chess from 'react-chess';
import './App.css'

function App() {
  function getRandomInt(max) {
    return Math.floor(Math.random() * Math.floor(max));
  }

  const chessPieces = ['R', 'Q', 'B', 'N', 'r', 'q', 'b', 'n'];
  const squares = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
  const numbers = ['1', '2', '3', '4', '5', '6', '7', '8'];

  const randomChessPieces = () => getRandomInt(chessPieces.length);
  const randomNumbers = () => getRandomInt(numbers.length);
  const randomSquares = () => getRandomInt(squares.length);
  const setBoard = [];
  const randomCoordenates = () => {

    const s = [];
    for (let i = 0; i < 20; i++) {
      s.push(squares[randomSquares()] + numbers[randomNumbers()])


    }

    const coo = [...new Set(s)];


    for (let i = 0; i < coo.length; i++) {
      setBoard.push(chessPieces[randomChessPieces()] + '@' + coo[i])

    }

    console.log(setBoard)
    return setBoard;
  }
  randomCoordenates()

  const randomKings = ['K' + '@' +
    squares[randomSquares()] +
    numbers[randomNumbers()],
  'k' + '@' +
  squares[randomSquares()] +
  numbers[randomNumbers()]
  ];
  setBoard.push(randomKings[0], randomKings[1])

  return (
    <div className='ChessBoard'>
      <Chess pieces={setBoard} />
    </div>

  );
}

export default App;
