import React, { useState } from 'react'
import Chess from 'react-chess'

const Moves = () => {

    function getRandomInt(max) {
        return Math.floor(Math.random() * Math.floor(max));
    }

    const [letter, setLetter] = useState(['a']);
    const [number, setNumber] = useState([1])

    const [position, handlePosition] = useState([]);

    const makeRandomMove = (e) => {
        const i = 1;
        setNumber(Number(number) + i);
        if (Number(number) >= 8) { setNumber(Number(number) - i) }

        console.log(number)
        handlePosition(['K@' + letter + number + e])


    }

    return (
        <div >
            <button onClick={(e) => makeRandomMove(e.target.value)} > Move </button>
            <Chess pieces={position} />

        </div>
    )
}

export default Moves;
