import React, { useState } from 'react'
import Chess from 'react-chess'

const ReadPGN = () => {
    const [file, handleFile] = useState([]);

    return (

        <div>
            <input
                type='file'
                onChange={(e) => handleFile(e.target.value)}
            />

            <br />
            <Chess />
            <br />

            <br />
            {file}
            <br />



        </div>
    )
}

export default ReadPGN;
