import React, { useState, useEffect } from 'react';
import Chess from 'react-chess';

const King = () => {

    const [letter, setLetter] = useState(['a']);
    const [number, setNumber] = useState(Number(1));
    const [piece, setPiece] = useState(['K@']);
    const [index, setIndex] = useState(Number(1))

    const [position, handlePosition] = useState([]);

    const makeKingMove = (e) => {

        const randomMove = () => {

            //vertical
            if (Number(Math.random()) > 0.49) {
                setNumber(Number(number) + 1)
            }
            else {
                setNumber(Number(number) - 1)
            }
            if (Number(number) >= 8) {
                setNumber(Number(number) - 1)
            }
            if (Number(number <= 1)) {
                setNumber(Number(number) + 1)
            }


        }


        const letters = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];

        if (Number(Math.random()) > 0.49) {
            setIndex(index + 1)
        }
        else {
            setIndex(index - 1)
        }

        if (Number(index) > 8) {
            setIndex(Number(7))
        }
        if (Number(index) < 0) {
            setIndex(Number(0))
        }

        setLetter(letters[index])



        randomMove();


        handlePosition([piece + letter + number]);
        console.log(number, letter, index);
    }

    return (
        <div >
            <button onClick={(e) => makeKingMove(e.target.value)} > Move </button>
            <Chess pieces={position} allowMoves={false} />

        </div>
    )
}

export default King;
