                return new Promise(resolve => setTimeout(resolve, ms));
            }

            for (let i = 1; i < 100; i++) {
                if (Math.random() * (i / 100) < Number(0.4999)) {
                    if (Number(number) > 7) {
                        setNumber(Number(number) - 1);
                        handlePosition([piece + letter + number]);
                        await sleep(10);
                    }
                    else {
                        setNumber(Number(number) + 1)
                        handlePosition([piece + letter + number]);
                        await sleep(10);
                    }
                    if (Number(number) < 2) {
                        setNumber(Number(number) + 1);
                        handlePosition([piece + letter + number]);
                        await sleep(10);
                    }

                    else {
                        setNumber(Number(number) - 1)
                        handlePosition([piece + letter + number]);
                        await sleep(10);
                    }

                }

                else {
                    if (Number(number) > 7) { setNumber(Number(number) - 1) }
                    if (Number(number) < 2) { setNumber(Number(number) + 1) }
                    handlePosition([piece + letter + number]);
                    await sleep(10);

                }

                console.log(position);

                console.log(i);
            }


        }
        randomMove()


    }

    return (
        <div >
            <button onClick={(e) => makeKingMove(e.target.value)} > Move </button>
            <Chess pieces={position} allowMoves={false} />

        </div>
    )
}

export default King;
