import React from 'react';
import Chess from 'react-chess';
import './App.css'

const randomBoard = (board = [], nPieces = 10) => {

  function getRandomInt(max) {
    return Math.floor(Math.random() * Math.floor(max));
  }

  const chessPieces = ['R', 'P', 'Q', 'B', 'N', 'r', 'p', 'q', 'b', 'n'];
  const letters = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
  const numbers = ['1', '2', '3', '4', '5', '6', '7', '8'];

  const randomChessPieces = () => getRandomInt(chessPieces.length);
  const randomNumbers = () => getRandomInt(numbers.length);
  const randomLetters = () => getRandomInt(letters.length);

  const randomKings = ['K@' + letters[randomLetters()] + numbers[randomNumbers()],
  'k@' + letters[randomLetters()] + numbers[randomNumbers()]
  ];


  board.push(randomKings[0], randomKings[1])

  const noRepeated = [];
  for (let i = 0; i < board.length; i++) {
    const b = String(board[i])

    noRepeated.push(b.slice(2))


  }

  const data = [...new Set(noRepeated)]


  for (let i = 0; i < data.length; i++) {
    const d = [chessPieces[randomChessPieces()] + '@' + data[i]]

    board.push(d[0])
  }

}


function App() {

  const board = ['R@h1'];
  randomBoard(board, 20)

  return (
    <div className='ChessBoard'>
      <Chess pieces={board} />
    </div>

  );
}

export default App;
