import React from 'react';
import Chess from 'react-chess';
import './App.css'

function getRandomInt(max) {
  return Math.floor(Math.random() * Math.floor(max));
}

function App() {

  const chessPieces = ['R', 'P', 'Q', 'B', 'N', 'r', 'p', 'q', 'b', 'n'];
  const squares = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
  const numbers = ['1', '2', '3', '4', '5', '6', '7', '8'];

  const randomChessPieces = () => getRandomInt(chessPieces.length);
  const randomNumbers = () => getRandomInt(numbers.length);
  const randomSquares = () => getRandomInt(squares.length);


  const randomKings = ['K' + '@' +
    squares[randomSquares()] +
    numbers[randomNumbers()],
  'k' + '@' +
  squares[randomSquares()] +
  numbers[randomNumbers()]
  ];


  const board = [];
  board.push(randomKings[0], randomKings[1])

  const randomBoard = () => {
    for (let i = 1; i < 10; i++) {
      const move = [chessPieces[randomChessPieces()] + '@' + squares[randomSquares()] + numbers[randomNumbers()]];
      board.push(move[0])

    }

    console.log(board);

  }
  randomBoard()

  return (
    <div className='ChessBoard'>
      <Chess pieces={board} />
    </div>

  );
}

export default App;
