import React from 'react';
import Chess from 'react-chess';
import './App.css'

function App() {
  const lineup = ['R@h1', 'P@f2', 'q@d8', 'R@a1', 'P@a2', 'P@c2', 'b@c8', 'p@d7', 'Q@d1', 'n@g8']

  const chessPieces = ['R', 'P', 'Q', 'K', 'B', 'N', 'r', 'p', 'q', 'k', 'b', 'n'];
  const squares = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
  const numbers = ['1', '2', '3', '4', '5', '6', '7', '8'];

  function getRandomInt(max) {
    return Math.floor(Math.random() * Math.floor(max));
  }

  const randomPiece = [chessPieces[getRandomInt(chessPieces.length)]
    + '@' + squares[getRandomInt(squares.length)] + numbers[getRandomInt(numbers.length)]];

  const randomKings = [chessPieces[3] + '@' + squares[0] + numbers[0],
  chessPieces[9] + '@' + squares[1] + numbers[0]
  ];




  return (
    <div className='ChessBoard'>
      <Chess pieces={randomKings} />
    </div>

  );
}

export default App;
