import React from 'react';
import Chess from 'react-chess';
import './App.css'

function getRandomInt(max) {
  return Math.floor(Math.random() * Math.floor(max));
}

function App() {

  const chessPieces = ['R', 'P', 'Q', 'B', 'N', 'r', 'p', 'q', 'b', 'n'];
  const squares = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
  const numbers = ['1', '2', '3', '4', '5', '6', '7', '8'];




  const randomSquares = () => {

    const a = getRandomInt(squares.length);

    return (a)
  }
  const randomNumbers = () => getRandomInt(numbers.length);


  const randomKings = ['K' + '@' +
    squares[randomSquares()] +
    numbers[randomNumbers()],
  'k' + '@' +
  squares[randomSquares()] +
  numbers[randomNumbers()]
  ];

  const randomPieces = [];

  const p = [];

  for (let i = 1; i < 10; i++) {
    const randomPiece = [chessPieces[getRandomInt(chessPieces.length + 1)]
      + '@' + squares[getRandomInt(squares.length + 1)] + numbers[getRandomInt(numbers.length + 1)]];

    randomPieces.push(randomPiece[0])

  }

  for (let i = 2; i < (numbers.length + 1); i++) {
    p.push('p' + '@' + squares[getRandomInt(squares.length)] + i)
    p.push('P' + '@' + squares[getRandomInt(squares.length)] + i)

  }

  for (let i = 0; i < 3; i++) {

    p.push(randomPieces[i])
  }

  p.push(randomKings[0], randomKings[1])


  const randomBoard = (set = []) => {
    const move = [set + squares[randomSquares()] + numbers[randomNumbers()]]
    console.log(move)

  }
  randomBoard([chessPieces])


  return (
    <div className='ChessBoard'>
      <Chess pieces={p} />
    </div>

  );
}

export default App;
