import React, { useState } from 'react'
import Chess from 'react-chess'
import myRandomMoves from './RandomMoves'

const Board = () => {

    const [position, handlePosition] = useState(myRandomMoves());



    const makeRandomMove = () => {







        handlePosition(myRandomMoves());


    }

    return (

        <div >

            <button onClick={() => makeRandomMove()} >
                Random
            </button>

            <Chess
                pieces={position}

                allowMoves={false} />
        </div>
    )


}

export default Board;
