import React, { useState, useEffect } from 'react'
import Chess from 'react-chess'

const King = () => {

    const [letter, setLetter] = useState(['e']);
    const [number, setNumber] = useState([5]);
    const [piece, setPiece] = useState(['K@']);


    const [position, handlePosition] = useState([]);

    const makeKingMove = (e) => {

        const randomMove = () => {
            if (Number(Math.random()) > 0.49) {
                setNumber(Number(number) + 1)
            }
            else {
                setNumber(Number(number) - 1)
            }
            if (Number(number) === 8) {
                setNumber(Number(number) - 1)
            }
            if (Number(number === 1)) {
                setNumber(Number(number) + 1)
            }
        }
        console.log(number)

        randomMove()
        handlePosition(piece + letter + Number(number))


    }


    return (
        <div >
            <button onClick={(e) => makeKingMove(e.target.value)} > Move </button>
            <Chess pieces={position} allowMoves={false} />

        </div>
    )
}

export default King;
