import React, { useState, useEffect } from 'react';
import Chess from 'react-chess';

const King = () => {

    const [letter, setLetter] = useState(['e']);
    const [number, setNumber] = useState(Number(5));
    const [piece, setPiece] = useState(['K@']);


    const [position, handlePosition] = useState([]);

    const makeKingMove = (e) => {

        const randomMove = () => {

            //vertical
            if (Number(Math.random()) > 0.49) {
                setNumber(Number(number) + 1)
            }
            else {
                setNumber(Number(number) - 1)
            }
            if (Number(number) === 8) {
                setNumber(Number(number) - 1)
            }
            if (Number(number === 1)) {
                setNumber(Number(number) + 1)
            }


        }
        randomMove();

        const randomRow = () => {

            const letters = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
            const index = 0;

            if (Number(Math.random()) > 0.49) {
                setLetter(letters[index])
            }
            else {
                setLetter(letters[index + 1])
            }
            if (letter === 'a') {
                setLetter(letters[index + 1])
            }
            if (letter === 'h') {
                setLetter(letters[index - 1])
            }
        }

        randomRow();



        handlePosition([piece + letter + number]);
        console.log(number, letter);
    }

    return (
        <div >
            <button onClick={(e) => makeKingMove(e.target.value)} > Move </button>
            <Chess pieces={position} allowMoves={false} />

        </div>
    )
}

export default King;
