import React, { useState } from 'react'
import Chess from 'react-chess'


const Board = () => {

    const [position, handlePosition] = useState(['R@h1']);

    const makeMove = () => {

        handlePosition(['R@h8']);


    }

    const makeMoveBack = () => {

        handlePosition(['R@h1']);

    }

    const makeRandomMove = () => {


        const move = []


        handlePosition(move);


    }

    return (

        <div >
            <button onClick={() => makeMove()} >
                Move
            </button>

            <button onClick={() => makeMoveBack()} >
                Back
            </button>
            <button onClick={() => makeRandomMove()} >
                Random
            </button>

            <Chess
                pieces={position}

                allowMoves={true} />
        </div>
    )


}

export default Board;
