import React, { useState } from 'react';
import Chess from 'react-chess';

const Knight = () => {
    const letters = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
    const [index, setIndex] = useState(Number(1));

    const [number, setNumber] = useState(1);
    const [piece] = useState(['N@']);
    const [position, handlePosition] = useState(['N@a1']);

    const makeKnightMove = () => {
        const randomMove = () => {



            if (Number(Math.random()) > 0.49) {

                if (Number(Math.random()) > 0.49) {
                    if (Number(Math.random()) > 0.49) {
                        setNumber(Number(number) + 2);
                        setIndex(index + 1);

                        console.log('1')
                    }
                    else {
                        setNumber(Number(number) + 2);
                        setIndex(index - 1);


                        console.log('2')

                    }
                }
                else {
                    if (Number(Math.random()) > 0.49) {
                        setNumber(Number(number) + 1);
                        setIndex(index + 2);

                        console.log('3')


                    }
                    else {
                        setNumber(Number(number) + 1);
                        setIndex(index - 2);
                        console.log('4')




                    }

                }

            }
            else {
                if (Number(Math.random()) > 0.49) {
                    if (Number(Math.random()) > 0.49) {
                        setNumber(Number(number) - 2);
                        setIndex(index + 1);

                        console.log('5')

                    }
                    else {
                        setNumber(Number(number) - 2);
                        setIndex(index - 1);

                        console.log('6')


                    }
                }
                else {
                    if (Number(Math.random()) > 0.49) {
                        setNumber(Number(number) - 1);
                        setIndex(index + 2);


                        console.log('7')

                    }
                    else {
                        setNumber(Number(number) - 1);
                        setIndex(index - 2);

                        console.log('1')



                    }

                }

                //corners

                if (Number(number) === 8 && Number(index) === 7) {

                    setNumber(Number(number) - 2)
                    setIndex(index - 1)


                }


                if (Number(number) === 1 && Number(index) === 0) {
                    setNumber(Number(number) - 2)
                    setIndex(index - 1)


                }
                if (Number(number) === 8 && Number(index) === 0) {

                    setNumber(Number(number) - 2)
                    setIndex(index + 1)


                }


                if (Number(number) === 1 && Number(index) === 7) {
                    setNumber(Number(number) + 2)
                    setIndex(index - 1)


                }




            }
            /*

        */

            console.log(position);
        }
        randomMove();
        handlePosition([piece + letters[index] + number]);


    }

    return (
        <div >
            <button onClick={(e) => makeKnightMove(e.target.value)} > Move </button>
            <Chess pieces={position} allowMoves={false} />

        </div>
    )
}

export default Knight;
